package PrimitiveDT;

public class CaseStudy {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		boolean var=true;
		System.out.println("boolean var="+var);

		short temp=-118;
		System.out.println("short datatype value="+temp);
		
		char alphabet='\u0035';
		System.out.println(" alphabet="+alphabet);
		
		int num=600;
		System.out.println("number ="+num);
		
		byte val=126;
		System.out.println("byte datatype value = "+val);
		
		long long_num=45903;
		System.out.println("long datatype number="+long_num);
		
		float float_num = 345.56f;
		System.out.println("float_num = "+float_num);
		

	}

}
